export type Data = {
    [key in typeof Variables[number]]: string
}

export type Condition = {
    variable: typeof Variables[number],
    operator: typeof Operators[number],
    value: string,
    param?: string
}

export interface Conditional {
    url: string,
    and: boolean
    conditions: Condition[],
    redirects?: number
}

export type User = {
    _id: string,
    uid: ObjectId,
    urls: string[],
    urlCount: number,
    hashedPassword: string,
    hashedRefresh: string
}

export type ShortURL = {
    _id: string, //short url
    uid: ObjectId, //used to connect with data points. Changes if the analytics are cleared
    conditionals: Conditional[],
    urlCount: number,
    owner: string,
    redirects: number[],
    firstPoint: number,
    deleted: boolean
}

interface DataPoint {
    _id: ObjectId,
    urlUID: ObjectId,
    [i: number]: number
}

export interface DataMin extends DataPoint {
    unixMin: number
}

export interface DataHour extends DataPoint {
    unixHour: number
}

export interface DataDay extends DataPoint {
    unixDay: number
}

export interface DataValue extends DataPoint {
    var: typeof Variables[number],
    val: string,
}
